/* eslint-disable */
import '../../../dist/synergy.js';
import { expect, fixture, html } from '@open-wc/testing';
import sinon from 'sinon';
import type SynIconButton from '../icon-button/icon-button.js';
import type SynTab from './tab.js';
import type SynTabGroup from '../tab-group/tab-group.js';

describe('<syn-tab>', () => {
  it('passes accessibility test', async () => {
    const el = await fixture<SynTab>(html`
      <syn-tab-group>
        <syn-tab slot="nav">Test</syn-tab>
      </syn-tab-group>
    `);
    await expect(el).to.be.accessible();
  });

  it('should render default tab', async () => {
    const el = await fixture<SynTab>(html` <syn-tab>Test</syn-tab> `);

    const base = el.shadowRoot!.querySelector<HTMLElement>('[part~="base"]')!;

    expect(el.getAttribute('role')).to.equal('tab');
    expect(el.getAttribute('aria-disabled')).to.equal('false');
    expect(el.getAttribute('aria-selected')).to.equal('false');
    expect(base.getAttribute('tabindex')).to.equal('0');
    expect(base.getAttribute('class')).to.equal(' tab ');
    expect(el.active).to.equal(false);
    expect(el.closable).to.equal(false);
    expect(el.disabled).to.equal(false);
  });

  it('should disable tab by attribute', async () => {
    const el = await fixture<SynTab>(html` <syn-tab disabled>Test</syn-tab> `);

    const base = el.shadowRoot!.querySelector<HTMLElement>('[part~="base"]')!;

    expect(el.disabled).to.equal(true);
    expect(el.getAttribute('aria-disabled')).to.equal('true');
    expect(base.getAttribute('class')).to.equal(' tab tab--disabled ');
    expect(base.getAttribute('tabindex')).to.equal('-1');
  });

  it('should set active tab by attribute', async () => {
    const el = await fixture<SynTab>(html` <syn-tab active>Test</syn-tab> `);

    const base = el.shadowRoot!.querySelector<HTMLElement>('[part~="base"]')!;

    expect(el.active).to.equal(true);
    expect(el.getAttribute('aria-selected')).to.equal('true');
    expect(base.getAttribute('class')).to.equal(' tab tab--active ');
    expect(base.getAttribute('tabindex')).to.equal('0');
  });

  it('should set closable by attribute', async () => {
    const el = await fixture<SynTab>(html` <syn-tab closable>Test</syn-tab> `);

    const base = el.shadowRoot!.querySelector<HTMLElement>('[part~="base"]')!;
    const closeButton = el.shadowRoot!.querySelector('[part~="close-button"]');

    expect(el.closable).to.equal(true);
    expect(base.getAttribute('class')).to.equal(' tab tab--closable ');
    expect(closeButton).not.to.be.null;
  });

  describe('focus', () => {
    it('should focus inner div', async () => {
      const el = await fixture<SynTab>(html` <syn-tab>Test</syn-tab> `);

      const base = el.shadowRoot!.querySelector<HTMLElement>('[part~="base"]')!;

      el.focus();
      await el.updateComplete;

      expect(el.shadowRoot!.activeElement).to.equal(base);
    });
  });

  describe('blur', () => {
    it('should blur inner div', async () => {
      const el = await fixture<SynTab>(html` <syn-tab>Test</syn-tab> `);

      el.focus();
      await el.updateComplete;

      el.blur();
      await el.updateComplete;

      expect(el.shadowRoot!.activeElement).to.equal(null);
    });
  });

  describe('closable', () => {
    it('should emit close event when the close button is clicked', async () => {
      const tabGroup = await fixture<SynTabGroup>(html`
        <syn-tab-group>
          <syn-tab slot="nav" panel="general" closable>General</syn-tab>
          <syn-tab slot="nav" panel="custom" closable>Custom</syn-tab>
          <syn-tab-panel name="general">This is the general tab panel.</syn-tab-panel>
          <syn-tab-panel name="custom">This is the custom tab panel.</syn-tab-panel>
        </syn-tab-group>
      `);
      const closeButton = tabGroup
        .querySelectorAll('syn-tab')[0]!
        .shadowRoot!.querySelector<SynIconButton>('[part~="close-button"]')!;

      const handleClose = sinon.spy();
      const handleTabShow = sinon.spy();

      tabGroup.addEventListener('syn-close', handleClose, { once: true });
      // The syn-tab-show event shouldn't be emitted when clicking the close button
      tabGroup.addEventListener('syn-tab-show', handleTabShow);

      closeButton.click();
      await closeButton?.updateComplete;

      expect(handleClose.called).to.equal(true);
      expect(handleTabShow.called).to.equal(false);
    });
  });
});
